value.string:
from Products.ERP5Type.DateUtils import addToDate, getClosestDate
 from DateTime import DateTime

 person = context
 portal = person.getPortalObject()

 if remove_hosting_list is None:
 remove_hosting_list = []
 if add_kw_list is None:
 add_kw_list = []
 if start_date_tuple_list is None:
 start_date_tuple_list = []
 if stop_date is not None:
 stop_date = DateTime(stop_date)

 previous_open_sale_order = portal.portal_catalog.getResultValue(
 default_destination_uid=person.getUid(),
 portal_type="Open Sale Order",
 validation_state="validated")

 hosting_subscription_mapping = {}
 if previous_open_sale_order is not None:
 for open_sale_order_line in previous_open_sale_order.contentValues(portal_type='Open Sale Order Line'):
 hosting_subscription_mapping[open_sale_order_line.getAggregate(portal_type='Hosting Subscription')] = open_sale_order_line.getId()

 now = DateTime()

 add_kw_kw = {}
 for (add_kw, explanation) in add_kw_list:
 existing = portal.portal_catalog.countResults(
 portal_type='Open Sale Order Line',
 default_aggregate_uid=portal.restrictedTraverse(add_kw['aggregate']).getUid()
 )[0][0] > 0
 if add_kw['aggregate'] not in hosting_subscription_mapping and not existing:
 add_kw_kw[add_kw['aggregate']] = add_kw.copy()
 add_kw_kw[add_kw['aggregate']]['explanation'] = ['Added because of %s ' % explanation]

 modify_kw_kw = {}
 for start_date_tuple in start_date_tuple_list:
 start_date_tuple, explanation = start_date_tuple[0:2], start_date_tuple[2]
 start_date = getClosestDate(target_date=start_date_tuple[1], precision='day')
 while start_date.day() >= 29:
 start_date = addToDate(start_date, to_add={'day': -1})
 start_date_modified = False
 if start_date_tuple[0] in hosting_subscription_mapping:
 line_id = hosting_subscription_mapping[start_date_tuple[0]]
 line = previous_open_sale_order[line_id]
 if line.getStartDate() is None:
 modify_kw_kw[line.getAggregate(portal_type='Hosting Subscription')] = {'start_date': start_date}
 modify_kw_kw[line.getAggregate(portal_type='Hosting Subscription')]['explanation'] = ['Set start date because of %s' % explanation]
 start_date_modified = True
 elif start_date_tuple[0] in add_kw_kw:
 add_kw_kw[start_date_tuple[0]]['start_date'] = start_date
 add_kw_kw[start_date_tuple[0]]['explanation'].append('Added start date beacuse of %s' % explanation)
 start_date_modified = True

 if previous_open_sale_order is not None:
 for line in previous_open_sale_order.contentValues(portal_type='Open Sale Order Line'):
 old_stop_date = line.getStopDate()
 if old_stop_date is not None:
 if stop_date is not None and old_stop_date < stop_date:
 modify_kw_kw.setdefault(line.getAggregate(portal_type='Hosting Subscription'), {})
 modify_kw_kw[line.getAggregate(portal_type='Hosting Subscription')].setdefault('explanation', [])
 modify_kw_kw[line.getAggregate(portal_type='Hosting Subscription')]['stop_date'] = stop_date
 modify_kw_kw[line.getAggregate(portal_type='Hosting Subscription')]['explanation'].append('Set stop date because of stop_date %s' % stop_date)
 elif old_stop_date < now:
 new_stop_date = addToDate(old_stop_date, to_add={'month': 1})
 while new_stop_date < now:
 new_stop_date = addToDate(new_stop_date, to_add={'month': 1})
 hosting_id = line.getAggregate(portal_type='Hosting Subscription')
 modify_kw_kw.setdefault(hosting_id, {})
 modify_kw_kw[line.getAggregate(portal_type='Hosting Subscription')].setdefault('explanation', [])
 modify_kw_kw[line.getAggregate(portal_type='Hosting Subscription')]['stop_date'] = new_stop_date
 modify_kw_kw[line.getAggregate(portal_type='Hosting Subscription')]['explanation'].append('Set stop date becase of new_stop_date %s' % new_stop_date)

 for (key, value) in add_kw_kw.copy().iteritems():
 if stop_date is not None:
 add_kw_kw[key]['stop_date'] = stop_date
 add_kw_kw[key]['explanation'].append('Set stop date beacuse of stop_date %s ' % stop_date)
 else:
 start_date = add_kw_kw[key].get('start_date')
 if start_date is not None:
 new_stop_date = addToDate(start_date, to_add={'month': 1})
 while new_stop_date < now:
 new_stop_date = addToDate(new_stop_date, to_add={'month': 1})
 add_kw_kw[key]['stop_date'] = new_stop_date
 add_kw_kw[key]['explanation'].append('Set stop date because of new_stop_date %s' % new_stop_date)

 for (key, value) in modify_kw_kw.copy().iteritems():
 if stop_date is not None:
 modify_kw_kw[key]['stop_date'] = stop_date
 modify_kw_kw[key]['explanation'].append('Set stop date because of stop_date %s' % stop_date)
 else:
 start_date = modify_kw_kw[key].get('start_date')
 if start_date is not None:
 new_stop_date = addToDate(start_date, to_add={'month': 1})
 while new_stop_date < now:
 new_stop_date = addToDate(new_stop_date, to_add={'month': 1})
 modify_kw_kw[key]['stop_date'] = new_stop_date
 modify_kw_kw[key]['explanation'].append('Set stop date because of new_stop_date %s' % new_stop_date)

 remove_id_list = set()
 if previous_open_sale_order is not None:
 for (remove_hosting, explanation) in remove_hosting_list:
 if remove_hosting in add_kw_kw:
 add_kw_kw[remove_hosting]['explanation'].append('Not removed because just added')
 continue
 if remove_hosting in modify_kw_kw:
 modify_kw_kw[remove_hosting]['explanation'].append('Not removed because just modified')
 continue
 if remove_hosting in hosting_subscription_mapping:
 line = previous_open_sale_order[hosting_subscription_mapping.get(remove_hosting)]
 if (line.getStartDate() is not None and line.getStopDate() is not None and line.getStartDate() < line.getStopDate()):
 remove_id_list.add((line.getId(), 'Removed %s' % line.getPath()))
 return add_kw_kw, modify_kw_kw, list(remove_id_list)
