from zExceptions import Unauthorized
 from AccessControl import getSecurityManager
 if REQUEST is None:
 raise Unauthorized

 response = REQUEST.RESPONSE
 type = 'application/json; class=slapos.org.hosting_subscription'

 if REQUEST.other['method'] != "POST":
 response.setStatus(405)
 return ""
 elif type != context.Base_getRequestHeader('Content-Type'):
 response.setStatus(406)
 return ""
 elif context.getPortalType() != "Person":
 response.setStatus(403)
 return ""
 else:
 import json
 try:
 data_dict = json.loads(context.Base_getRequestBody())
 except (TypeError, ValueError):
 response.setStatus(400)
 return ""
 else:

 def dictToXml(dict_data):
 assert same_type(dict_data, {})
 result = "<?xml version='1.0' encoding='utf-8'?><instance>\n"
 for key, value in dict_data.items():
 result += """ <parameter id="%s">%s</parameter>\n""" % (key, value)
 result += "</instance>"
 return result

 try:
 parameter_kw = {
 'software_release': data_dict['software_release'],
 'software_title': data_dict['title'],
 'software_type': data_dict['software_type'],
 'instance_xml': dictToXml(data_dict['parameter']),
 'sla_xml': dictToXml(data_dict['sla']),
 'shared': data_dict['slave'],
 'state': data_dict['status'],
 }
 except KeyError:
 response.setStatus(400)
 return ""
 else:

 context.requestSoftwareInstance(**parameter_kw)
 # XXX Return hosting subscription link
 response.setStatus(201)
 return ""
