        })\n
        .push(function () {\n
          var form = gadget.property_dict.element.querySelector(\'form.search\'),\n
            refresh = gadget.property_dict.element.querySelector(\'.listbox-refresh\'),\n
            filter = gadget.property_dict.element.querySelector(\'.listbox-filter\'),\n
            form_filter = gadget.property_dict.element.querySelector(\'form.filter\'),\n
            promise_list = [];\n
\n
          if (form !== undefined && form !== null && form !== \'\') {\n
            promise_list.push(loopEventListener(\n
